import { getHeroeById } from "./bases/08-import-export";
//Promesas
const promesa = new Promise( (resolve, reject) => {
    setTimeout(() => {
        //Tarea
        const heroe = getHeroeById(2);
        resolve(heroe);
    }, 2000);
});

promesa.then( (heroe) => {
    console.log('Then de la promesa ', heroe);
})
