const {nombre, edad, clave} = persona;

/* console.log(nombre);
console.log(edad);
console.log(clave);
console.log(nombre2); */

const retornaPersona = (nombre, edad, rango = 'Capitán') => {
    console.log(nombre, edad, rango);
}

retornaPersona(persona);
