//Promesas
const promesa = new Promise( (resolve, reject) => {
    setTimeout(() => {
        //Tarea
        const getHeroeById = (id) => {
            return heroes.find( heroe => heroe.id===id);
        }
        const heroe = getHeroeById(2);
        resolve(heroe);
    }, 2000);
});

promesa.then( (heroe) => {
    console.log('Then de la promesa ', heroe)
})
